import '../Styles/Home.css';
import '../Styles/Menu.css';
import React, {useState} from "react";
import {BrowserRouter as Router, Link, Routes, Route} from 'react-router-dom';
import Bio from './Bio.js';
import Home from './Home.js';

function Menu() {
    const [isMenuOpen,
        setIsMenuOpen] = useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    return (

        <div>

            <div className="menu">
                <div className="menu-left">
                    <Link to="/">
                        <img className='logoim1' src="/images/imagegroupbee.png" alt="Image 1"/>
                    </Link>
                </div>
                <div className="menu-right">
                    <Link to="bio">Bio</Link>
                    <Link to="atelier">Ateliers d'Ã©criture</Link>
                    <Link to="tarif">Tarifs</Link>
                    <Link to="presse">Presse</Link>
                    <Link to="contact">Contact</Link>
                </div>

            </div>
            <div className="top-bar"></div>
            <div className="menu-container">
                {isMenuOpen
                    ? ( <> <img
                        src="new-menu-icon.png"
                        alt="Menu Icon"
                        className="menu-icon"
                        onClick={toggleMenu}/> < div className = "menu-text" > <a href="#">Lien 1</a> < a href = "#" > Lien 2 < /a>
              <a href="#">Lien 3</a > </div> < />
        ) : (
          <div className="menu-items" onClick={toggleMenu}>
            <img
              src="menu-icon.png"
              alt="Menu Icon"
              className="menu-icon"
            / > </div>)}
            </div>
        </div>
    );
}

export default Menu;
