import Footer from './Footer';
import React, {useState, useRef} from 'react';
import '../Styles/Podcast.css';

function Podcast() {
    const [currentlyPlaying,
        setCurrentlyPlaying] = useState(null);
    const [isPaused,
        setIsPaused] = useState(true); // Ajoutez un état pour suivre la pause
    const audioRef = useRef(null);

    const podcasts = [
        {
            title: "Podcast 1",
            url: "/podcasts/Episode1.mp3",
            date: "Date du podcast 1"
        }, {
            title: "Podcast 2",
            url: "/podcasts/Episode2.mp3",
            date: "Date du podcast 2"
        }, {
            title: "Podcast 3",
            url: "/podcasts/Episode3.mp3",
            date: "Date du podcast 3"
        }, {
            title: "Podcast 4",
            url: "/podcasts/Episode4.mp3",
            date: "Date du podcast 4"
        }, {
            title: "Podcast 5",
            url: "/podcasts/Episode4.mp3",
            date: "Date du podcast 4"
        },
        // Ajoutez d'autres podcasts ici
    ];

    const togglePlayPause = (url) => {
        const audio = audioRef.current;
        if (currentlyPlaying === url) {
            if (isPaused) {
                audio.play();
                setIsPaused(false);
            } else {
                audio.pause();
                setIsPaused(true);
            }
        } else {
            audio.src = url;
            audio.play();
            setCurrentlyPlaying(url);
            setIsPaused(false);
        }
    };

    const downloadPodcast = (url) => {
        const link = document.createElement('a');
        link.href = url;
        link.download = 'podcast.mp3';
        link.click();
    };

    return (
        <div>
            <div className='titrepodcast'>
                <h1>Podcasts</h1>
            </div>

            <div className='podcastbl1'>
                <div>
                    <img src='/images/pressbl1.png'/>
                </div>

                <div className='pressbl1bl2'>
                    <h3>Dans ma poche,<br></br>
                        ma mémoire
                    </h3>
                    <p>
                        <span>Novembre 2021 - Mars 2022</span>
                        <br></br>
                        -
                        <br></br>
                        "Dans ma poche, ma mémoire", un podcast en 5 épisodes réalisé par Charlie
                        Droesch-Du Cerceau durant les ateliers d'écriture menés à l'EHPAD du Centre
                        Hospitalier d'Erstein. Projet initié par la Compagnie Actémo Théâtre en
                        collaboration avec le Service Culturel de la Ville d'Erstein."Dans ma poche, ma
                        mémoire", un podcast en 5 épisodes réalisé par Charlie Droesch-Du Cerceau durant
                        les ateliers d'écriture menés à l'EHPAD du Centre Hospitalier d'Erstein. Projet
                        initié par la Compagnie Actémo Théâtre en collaboration avec le Service Culturel
                        de la Ville d'Erstein.
                    </p>
                </div>
            </div>

            <div className="podcast-page">
                {podcasts.map((podcast) => (
                    <div key={podcast.url} className="podcast-item">
                        <h3>{podcast.title}</h3>
                        <p>Date: {podcast.date}</p>
                        <button
               className="play-button"
               onClick={() => togglePlayPause(podcast.url)}
             >
               {currentlyPlaying === podcast.url && !isPaused // Utilisez l'état de pause
                 ? 'Pause'
                 : 'Écouter'}
             </button>
             <button
               className="download-button"
               onClick={() => downloadPodcast(podcast.url)}
             >
               Télécharger
             </button>
                    </div>
                ))}
            </div>

            <audio ref={audioRef}/>
            <Footer/>
        </div>
    );
}

export default Podcast;
