import '../Styles/Home.css';
import '../Styles/Menu.css';
import React from 'react';
import {BrowserRouter as Router, Link, Routes, Route} from 'react-router-dom';
import Bio from './Bio.js';
import Home from './Home.js';

function Menu() {
    const menuIcon = document.getElementById("menu-icon");
    const menuItems = document.getElementById("menu-items");

    let isOpen = false;

    menuIcon.addEventListener("click", () => {
        isOpen = !isOpen;
        if (isOpen) {
            menuItems.style.display = "block";
            menuIcon.style.transform = "translateY(100px)";
            menuIcon.src = "new-menu-icon.png"; // Changer l'image du menu hamburger
        } else {
            menuItems.style.display = "none";
            menuIcon.style.transform = "translateY(0)";
            menuIcon.src = "menu-icon.png"; // Revenir à l'image du menu hamburger d'origine
        }
    });

    return (

        <div>

            <div className="menu">
                <div className="menu-left">
                    <Link to="/">
                        <img className='logoim1' src="/images/imagegroupbee.png" alt="Image 1"/>
                    </Link>
                </div>
                <div className="menu-right">
                    <Link to="bio">Bio</Link>
                    <Link to="atelier">Ateliers d'écriture</Link>
                    <Link to="tarif">Tarifs</Link>
                    <Link to="presse">Presse</Link>
                    <Link to="contact">Contact</Link>
                </div>

            </div>
            <div class="top-bar"></div>
            <div class="top-bar"></div>
    <div class="menu-container">
        <div class="menu-items" id="menu-items">
            <a href="#">Lien 1</a>
            <a href="#">Lien 2</a>
            <a href="#">Lien 3</a>
        </div>
        <img src="menu-icon.png" alt="Menu Icon" class="menu-icon" id="menu-icon"/>
    </div>

        </div>
    );
}

export default Menu;
