import Footer from './Footer';
import React, { useState } from 'react';
import '../Styles/Podcast.css';

function Podcast() {
  const [currentlyPlaying, setCurrentlyPlaying] = useState(null);

  const podcasts = [
    {
      title: "Podcast 1",
      url: "/podcasts/Episode1.mp3",
      date: "Date du podcast 1",
    },
    {
      title: "Podcast 2",
      url: "/podcasts/Episode2.mp3",
      date: "Date du podcast 2",
    },
    {
      title: "Podcast 3",
      url: "/podcasts/Episode3.mp3",
      date: "Date du podcast 3",
    },
    {
      title: "Podcast 4",
      url: "/podcasts/Episode4.mp3",
      date: "Date du podcast 4",
    },
    // Ajoutez d'autres podcasts ici
  ];

  const togglePlayPause = (url) => {
    if (currentlyPlaying && currentlyPlaying.src === url) {
      // Si l'audio actuellement en cours de lecture correspond à celui cliqué,
      // vérifiez s'il est en pause, sinon mettez-le en pause.
      if (currentlyPlaying.paused) {
        currentlyPlaying.play();
      } else {
        currentlyPlaying.pause();
      }
    } else {
      // Si l'audio est différent de celui actuellement en cours de lecture,
      // arrêtez la lecture de l'ancien et commencez à lire le nouveau.
      if (currentlyPlaying) {
        currentlyPlaying.pause();
      }

      const audio = new Audio(url);
      audio.play();
      setCurrentlyPlaying(audio);
    }
  };

  const downloadPodcast = (url) => {
    window.open(url, '_blank');
  };

  return (
    <div>
      <div className='titrepodcast'>
        <h1>Podcasts</h1>
      </div>

      <div className='podcastbl1'>
        {/* Contenu de votre premier podcast */}
      </div>

      <div className="podcast-page">
        <h1>Liste de Podcasts</h1>
        {podcasts.map((podcast) => (
          <div key={podcast.url} className="podcast-item">
            <h3>{podcast.title}</h3>
            <p>Date: {podcast.date}</p>
            <button
              className="play-button"
              onClick={() => togglePlayPause(podcast.url)}
            >
              {currentlyPlaying && currentlyPlaying.src === podcast.url && !currentlyPlaying.paused
                ? 'Pause'
                : 'Écouter'}
            </button>
            <button
              className="download-button"
              onClick={() => downloadPodcast(podcast.url)}
            >
              Télécharger
            </button>
          </div>
        ))}
      </div>

      <Footer />
    </div>
  );
}

export default Podcast;
