import Footer from './Footer';
import React, { useState, useEffect } from 'react';
import '../Styles/Podcast.css';
import { Howl, Howler } from 'howler';

function Podcast() {
  const [currentlyPlaying, setCurrentlyPlaying] = useState(null);

  useEffect(() => {
    Howler.autoUnlock = true; // Débloque automatiquement l'audio sur les appareils mobiles

    return () => {
      // Arrête la lecture audio lorsque le composant est démonté
      if (currentlyPlaying) {
        currentlyPlaying.stop();
      }
    };
  }, [currentlyPlaying]);

  const podcasts = [
    {
      title: "Podcast 1",
      url: "/podcasts/Episode1.mp3",
      date: "Date du podcast 1",
    },
    {
      title: "Podcast 2",
      url: "/podcasts/Episode2.mp3",
      date: "Date du podcast 2",
    },
    {
      title: "Podcast 3",
      url: "/podcasts/Episode3.mp3",
      date: "Date du podcast 3",
    },
    {
      title: "Podcast 4",
      url: "/podcasts/Episode4.mp3",
      date: "Date du podcast 4",
    },
    // Ajoutez d'autres podcasts ici
  ];

  const togglePlayPause = (url) => {
    if (currentlyPlaying && currentlyPlaying.url() === url) {
      if (currentlyPlaying.playing()) {
        currentlyPlaying.pause();
      } else {
        currentlyPlaying.play();
      }
    } else {
      if (currentlyPlaying) {
        currentlyPlaying.stop();
      }

      const sound = new Howl({
        src: [url],
        onend: () => {
          setCurrentlyPlaying(null);
        },
      });

      sound.play();
      setCurrentlyPlaying(sound);
    }
  };

  const downloadPodcast = (url) => {
    // Téléchargez le fichier directement en ouvrant une nouvelle fenêtre
    window.open(url, '_blank');
  };

  return (
    <div>
      <div className='titrepodcast'>
        <h1>Podcasts</h1>
      </div>

      <div className='podcastbl1'>
        {/* Contenu de votre premier podcast */}
      </div>

      <div className="podcast-page">
        <h1>Liste de Podcasts</h1>
        {podcasts.map((podcast) => (
          <div key={podcast.url} className="podcast-item">
            <h3>{podcast.title}</h3>
            <p>Date: {podcast.date}</p>
            <button
              className="play-button"
              onClick={() => togglePlayPause(podcast.url)}
            >
              {currentlyPlaying && currentlyPlaying.url() === podcast.url
                ? currentlyPlaying.playing()
                  ? 'Pause'
                  : 'Play'
                : 'Play'}
            </button>
            <button
              className="download-button"
              onClick={() => downloadPodcast(podcast.url)}
            >
              Télécharger
            </button>
          </div>
        ))}
        {currentlyPlaying && (
          <div className="currently-playing">
            En train d'écouter : {currentlyPlaying.url()}
          </div>
        )}
      </div>

      <Footer />
    </div>
  );
}

export default Podcast;
