import { Link } from 'react-router-dom';

function Menu() {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  return (
    <nav className={`menu ${isMenuOpen ? 'menu-open' : ''}`}>
      <div className="menu-left">
        <Link to="/">
          <img className='logoim1' src="/images/imagegroupbee.png" alt="Image 1"/>
        </Link>
      </div>
      <div className={`menu-toggle ${isMenuOpen ? 'menu-toggle-open' : ''}`} onClick={toggleMenu}>
        <div className={`bar ${isMenuOpen ? 'bar-open' : ''}`}></div>
        <div className={`bar ${isMenuOpen ? 'bar-open' : ''}`}></div>
        <div className={`bar ${isMenuOpen ? 'bar-open' : ''}`}></div>
      </div>
      <div className={`menu-links ${isMenuOpen ? 'menu-links-open' : ''}`}>
        <Link to="bio">Bio</Link>
        <Link to="atelier">Ateliers d'Ã©criture</Link>
        <Link to="tarif">Tarifs</Link>
        <Link to="presse">Presse</Link>
        <Link to="contact">Contact</Link>
      </div>
    </nav>
  );
}

export default Menu;
