import Footer from './Footer';
import React, {useState} from 'react';
import sendEmail from './sendEmail';
import '../Styles/Podcast.css'

function Podcast() {
    const podcastsData = [
        {
            title: 'Podcast 1',
            audioUrl: 'URL_de_l_audio1.mp3',
            downloadUrl: 'URL_de_telechargement1.mp3'
        }, {
            title: 'Podcast 2',
            audioUrl: 'URL_de_l_audio2.mp3',
            downloadUrl: 'URL_de_telechargement2.mp3'
        },
        // Ajoutez plus d'objets pour vos autres podcasts
    ];
    const [selectedPodcast,
        setSelectedPodcast] = useState(null);

    const playPodcast = (audioUrl) => {
        const audio = new Audio(audioUrl);
        audio.play();
    };

    const downloadPodcast = (downloadUrl) => {
        // Mettez en œuvre la logique de téléchargement ici, par exemple, ouvrez un
        // nouvel onglet avec le lien de téléchargement
        window.open(downloadUrl, '_blank');
    };

    return (
        <div>
            <div className='titrepodcast'>
                <h1>Podcasts</h1>

            </div>
            <div className='podcastbl1'>
                <div>
                    <img src='/images/pressbl1.png'/>
                </div>

                <div className='podcastbl1bl2'>
                    <h3>Les ateliers d'écriture en confinement
                    </h3>
                    <p>
                        <span>16/10/2020</span>
                        <br></br>
                        -
                        <br></br>
                        Un reportage de Sylvie Malal sur les ateliers d'écriture en EHPAD diffusé dans
                        le 19/20 de France 3 Alsace
                    </p>
                </div>
            </div>

            <div className="podcast-page">
                <h1>Liste de Podcasts</h1>
                <ul className="podcast-list">
                    {podcastsData.map((podcast, index) => (
                        <li key={index} className="podcast-item">
                            <h3>{podcast.title}</h3>
                            <div className="podcast-buttons">
                                <button className="play-button" onClick={() => playPodcast(podcast.audioUrl)}>
                                    Écouter
                                </button>
                                <button
                                    className="download-button"
                                    onClick={() => downloadPodcast(podcast.downloadUrl)}>
                                    Télécharger
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
                {selectedPodcast && (
                    <div className="selected-podcast">
                        <h2>Podcast sélectionné : {selectedPodcast.title}</h2>
                        <button onClick={() => setSelectedPodcast(null)}>Fermer</button>
                    </div>
                )}
            </div>

            <Footer/>
        </div>
    );
}

export default Podcast;
