import Footer from './Footer';
import React, { useState } from 'react';
import sendEmail from './sendEmail';
import '../Styles/Podcast.css'



function Podcast() {
  const [currentlyPlaying, setCurrentlyPlaying] = useState(null);

  const playPodcast = (filename) => {
    const audioUrl = `/podcasts/${filename}.mp3`; // Chemin vers le fichier MP3
    const audio = new Audio(audioUrl);
    audio.play();
    setCurrentlyPlaying(filename);
  };

  const downloadPodcast = (filename) => {
    const audioUrl = `/podcasts/${filename}.mp3`; // Chemin vers le fichier MP3
    window.open(audioUrl, '_blank');
  };


  return (
    <div>
        <div className='titrepodcast'>
                <h1>Podcasts</h1>

            </div>
            <div className='podcastbl1'>
                <div>
                <img src='/images/pressbl1.png'/>
                </div>


                <div className='podcastbl1bl2'>
                    <h3>Les ateliers d'écriture en confinement
                    </h3>
                    <p>
                        <span>16/10/2020</span>
                        <br></br>
                        -
                        <br></br>
                        Un reportage de Sylvie Malal sur les ateliers d'écriture en EHPAD diffusé dans
                        le 19/20 de France 3 Alsace
                    </p>
                </div>
            </div>

            <div className="podcast-page">
      <h1>Liste de Podcasts</h1>
      {podcasts.map((podcast) => (
        <div key={podcast.filename} className="podcast-item">
          <h3>{podcast.title}</h3>
          <button
            className="play-button"
            onClick={() => playPodcast(podcast.filename)}
          >
            Écouter
          </button>
          <button
            className="download-button"
            onClick={() => downloadPodcast(podcast.filename)}
          >
            Télécharger
          </button>
        </div>
      ))}
      {currentlyPlaying && (
        <div className="currently-playing">
          En train d'écouter : {currentlyPlaying}
        </div>
      )}
    </div>




     <Footer/>
    </div>
  );
}

export default Podcast;
